  usersServices = inject (UsersService)
  arrUsers : IUser [] = []
  page : number = 1
  totalPages : number = 1

  // 
  // 
  ngOnInit () {
    this.usersServices.getAll(this.page).subscribe((data: IData) => {
      this.arrUsers = data.results
      this.page = data.page
      this.totalPages = data.total_pages
